<script>
import ConnectWallet from "./Modals/ConnectWallet";
import AboutModal from "./Modals/AboutModal";
import {mapGetters, mapMutations} from "vuex";
import ErrorModal from "./Modals/ErrorModal";
import Preloader from "./Preloader";

export default {
  name: "AppHeader",
  components: {
    Preloader,
    ErrorModal,
    ConnectWallet,
    AboutModal,

  },
  data: () => ({
    isOpenMenu: false,
    isOpenConnect: false,
    isOpenSelect: false,
    isAboutModalVisible: false,
    isShowAccInfo: false,
    isTooltip: false,
    error: null,
    loading: false,
    isOpenCoinSelect: false,
    isShowCoinInfo: false,
    currentCoin: 'BSC',
    currentCoinImg: '/img/coin/BNB.png',
    coinMap: [
      {
        coinImg: '/img/coin/BNB.png',
        coinName: 'BSC',
        isCurrent: false,
      },
      {
        coinImg: '/img/coin/ETH.png',
        coinName: 'ETH',
        isCurrent: false,
      },
      {
        coinImg: '/img/coin/MATIC.png',
        coinName: 'POLYGON',
        isCurrent: false,
      },
      {
        coinImg: '/img/coin/HARMONY.png',
        coinName: 'HARMONY',
        isCurrent: false,
      },
      {
        coinImg: '/img/coin/FANTOM.png',
        coinName: 'FANTOM',
        isCurrent: false,
      },

      {
        coinImg: '/img/coin/AVALANCE.png',
        coinName: 'AVALANCHE',
        isCurrent: false,
      },
      {
        coinImg: require('@/assets/img/combined.png'),
        coinName: 'COMBINED',
        isCurrent: false,
      }
    ]
  }),
  computed: {
    ...mapGetters({
      getAccount: 'wallet/getAccount',
      getBalance: 'wallet/getBalance'
    })
  },
  filters: {
    cutHash(v) {
      if (v) {
        let valStart = v.substring(0, 6)
        let valEnd = v.substring(v.length - 5, v.length);
        return `${valStart}...${valEnd}`
      } else {
        return ''
      }
    }
  },
  watch: {
    $route() {
      window.location.reload();
    }
  },
  methods: {
    ...mapMutations({
      unsetWallet: 'wallet/unsetWallet'
    }),
    resizeMobileNav() {
      let vh = window.innerHeight * 0.01;
      document.documentElement.style.setProperty('--vh', `${vh}px`);
    },
    setError(err) {
      this.error = err;
    },
    copy() {
      let value = this.$refs.address.value;
      navigator.clipboard.writeText(value)
          .then(() => this.isTooltip = true)
          .catch(() => this.isTooltip = false)
      setTimeout(() => {
        this.isTooltip = false
      }, 2000);
    },
  },
  mounted() {
    document.body.addEventListener('click', (e) => {
      if (!e.target.closest('.select__btn') && !e.target.closest('.select__drop')) {
        this.isOpenSelect = false;
        this.isOpenCoinSelect = false;
      }
      if (!e.target.closest('.burger-btn') && !e.target.closest('.mobile-nav')) {
        this.isOpenMenu = false;
      }
      if (!e.target.closest('.openAcc')) {
        this.isShowAccInfo = false;
      }
      if (!e.target.closest('.coin-select--mobile')) {
        this.isShowCoinInfo = false;
      }
    }),
        window.addEventListener('resize', () => {
          let vh = window.innerHeight * 0.01;
          document.documentElement.style.setProperty('--vh', `${vh}px`);
        });
  }
}
</script>

